mapper.insert:
INSERT INTO
prebury(

)
VALUES (#{id}, #{contractId},#{preBuryMan},#{count},#{preBuryTime},#{preBuryStatus},#{image},#{appId});

    select
    LAST_INSERT_ID() as id

mapper.select:
select prebury.*,contract.name as contractName,contract.customerName
     from prebury
     LEFT JOIN contract ON prebury.contractId=contract.id

         prebury.appId=#{appId}
         AND prebury.contractId=#{contractId}

  order by prebury.id desc

mapper.update:
UPDATE prebury
PREBURYMAN=#{preBuryMan}, PREBURYSTATUS=#{preBuryStatus}, COUNT=#{count}, IMAGE=#{image}, PREBURYTIME=#{preBuryTime},
WHERE ID=#{id}

mapper.select:
select id
from prebury
where CONTRACTID=#{contractId}

select prebury.*,contract.name as contractName,contract.customerName
from prebury,contract
where prebury.contractId=contract.id
 AND prebury.APPID=#{appId}
AND prebury.preBuryMan like CONCAT('%',#{user},'%') 
 AND prebury.preBuryTime>=#{startTime}
 AND prebury.preBuryTime<=#{endTime}  
order by prebury.id desc

select prebury.*,contract.name as contractName,contract.customerName
from prebury,contract
where prebury.contractId=contract.id
 AND prebury.APPID=#{appId}
AND prebury.preBuryMan like CONCAT('%',#{user},'%') 
AND contract.name like CONCAT('%',#{device},'%')
 AND prebury.preBuryTime>=#{startTime}
 AND prebury.preBuryTime<=#{endTime}  
AND contract.name LIKE #{contractName}
order by prebury.id desc
